sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }
	var getEmailAdr = new GlideAjax('GetEmailAdress');
	getEmailAdr.addParam('sysparm_name', 'getEmail');
	getEmailAdr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	getEmailAdr.getXML(populateEmailField);

	function populateEmailField(response){
		var emailFromInclude = response.responseXML.documentElement.getAttribute('answer');
		g_form.clearValue('u_email');
		g_form.setValue('u_email', emailFromInclude);
	}

   //Type appropriate comment here, and begin script below

}
